import React, { useState } from "react";
import signUpImage from "./undraw_window_shopping_b96y.svg";
import { useNavigate } from "react-router";
import {
  RiLoginCircleFill,
  AiFillEye,
  AiFillEyeInvisible,
} from "../icons/icon";
import styles from "./Login.module.css";

const SignUp = () => {
  const navigate = useNavigate();

  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  const handleSignUp = () => {};

  return (
    <div className={styles.loginDiv}>
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <div
          className={`inpt-div flex flex-col`}
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            width: "100%",
            margin: "1em 0em",
          }}
        >
          <input
            className={styles.inpt}
            type="text"
            required
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <label className={styles.inpt_label_secondary}>Enter Username</label>
        </div>

        <div
          className={`inpt-div flex flex-col`}
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            width: "100%",
            margin: "1em 0em",
          }}
        >
          <input
            className={styles.inpt}
            styles={{ margin: "1em 0em" }}
            required
            type="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <label className={styles.inpt_label_secondary}>Enter Email</label>
        </div>

        <div
          className={`inpt-div flex flex-col`}
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            width: "100%",
            margin: "1em 0em",
          }}
        >
          <input
            className={styles.inpt}
            styles={{ margin: "1em 0em" }}
            required
            type={showPassword ? "text" : "password"}
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <label className={styles.inpt_label_secondary}>Enter Password</label>
        </div>

        <div
          className={`inpt-div flex flex-col`}
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            width: "125%",
            margin: "1em 0em",
          }}
        >
          <input
            className={styles.inpt}
            styles={{ margin: "1em 0em" }}
            required
            type={showConfirmPassword ? "text" : "password"}
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
          <label className={styles.inpt_label_secondary}>
            Confirm Password
          </label>
        </div>

        <button className={`btn btn-dark m-1`} onClick={handleSignUp}>
          SIGNUP
          <RiLoginCircleFill
            style={{
              fontSize: "1.1rem",
              transform: "translate(40%,25%)",
              margin: "0em",
              padding: "0em",
            }}
          />
        </button>
        <button
          style={{
            fontSize: "0.7rem",
            fontWeight: "900",
            letterSpacing: "1.5px",
          }}
          onClick={() => navigate("/login")}
        >
          Already Registered? LOGIN
        </button>
      </div>
      <div
        style={{
          backgroundColor: "rgb(80, 80, 80)",
          color: "#fff",
          display: "flex",
          padding: "3em 0em",
          justifyContent: "center",
          flexDirection: "column",
        }}
      >
        <h1> SIGNUP TO BARAK MART </h1>
        <img
          src={signUpImage}
          alt="form"
          style={{ width: "50%", alignSelf: "center", margin: "0.8em 0em" }}
        />
      </div>
    </div>
  );
};

export { SignUp };
