import React, { useState } from "react";
import signUpImage from "./undraw_window_shopping_b96y.svg";
import { useNavigate } from "react-router";
import axios from "axios";
import {
  checkInputField,
  validateEmail,
  validatePassword,
  validateUsername,
  isMatch,
} from "../utils";

import { USER_API } from "../urls";

import {
  RiLoginCircleFill,
  AiFillEye,
  AiFillEyeInvisible,
} from "../icons/icon";
import styles from "./Login.module.css";
import { IconContext } from "react-icons";

const SignUp = () => {
  const navigate = useNavigate();

  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [error, setError] = useState("");

  const handleSignUp = async (username, email, password) => {
    if (checkInputField(username) === "EMPTY") {
      return setError(`username cannot be Empty`);
    }

    if (checkInputField(email) === "EMPTY") {
      return setError(`email cannot be Empty`);
    }

    if (checkInputField(password) === "EMPTY") {
      return setError(`password cannot be Empty`);
    }

    if (!isMatch(password, confirmPassword)) {
      return setError("Password and ConfirmPassword Should Match");
    }

    if (!validateEmail(email)) {
      return setError("Enter a valid Email");
    }

    if (!validatePassword(password)) {
      return setError(
        "Password should contain atleast 6 characters of atleast lowercase, uppercase and numeric integer"
      );
    }

    if (!validateUsername(username)) {
      return setError("Enter a valid Username");
    }

    const response = await axios.post(USER_API, { username, email, password });

    console.log("handleSignUp", response);
  };

  return (
    <div className={styles.loginDiv}>
      <div className={styles.loginWrapper}>
        <div className={`inpt-div flex flex-col ${styles.inputWrapper}`}>
          <input
            className={styles.inpt}
            type="text"
            required
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <label className={styles.inpt_label_secondary}>Username</label>
        </div>

        <div className={`inpt-div flex flex-col ${styles.inputWrapper}`}>
          <input
            className={`${styles.inpt}`}
            required
            type="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <label className={styles.inpt_label_secondary}>Email</label>
        </div>

        <div className={`inpt-div flex flex-col  ${styles.inputWrapper}`}>
          <input
            className={`${styles.inpt}`}
            required
            type={showPassword ? "text" : "password"}
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <label className={styles.inpt_label_secondary}>Password</label>
          <IconContext.Provider value={{ size: "1.3em" }}>
            {showPassword ? (
              <AiFillEyeInvisible
                className={styles.passwordIcon}
                onClick={(e) =>
                  setShowPassword((showPassword) => !showPassword)
                }
              />
            ) : (
              <AiFillEye
                className={styles.passwordIcon}
                onClick={(e) =>
                  setShowPassword((showPassword) => !showPassword)
                }
              />
            )}
          </IconContext.Provider>
        </div>

        <div className={`inpt-div flex flex-col  ${styles.inputWrapper}`}>
          <input
            className={`${styles.inpt}`}
            required
            type={showConfirmPassword ? "text" : "password"}
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
          <label className={styles.inpt_label_secondary}>
            Confirm Password
          </label>
          <IconContext.Provider value={{ size: "1.3em" }}>
            {showConfirmPassword ? (
              <AiFillEyeInvisible
                className={styles.passwordIcon}
                onClick={(e) =>
                  setShowConfirmPassword(
                    (showConfirmPassword) => !showConfirmPassword
                  )
                }
              />
            ) : (
              <AiFillEye
                className={styles.passwordIcon}
                onClick={(e) =>
                  setShowConfirmPassword(
                    (showConfirmPassword) => !showConfirmPassword
                  )
                }
              />
            )}
          </IconContext.Provider>
        </div>

        <button className={`btn btn-dark m-1`} onClick={handleSignUp}>
          SIGNUP
          <RiLoginCircleFill className={styles.signupIcon} />
        </button>
        {error.length > 0 && <span>{error}</span>}
        <button
          className={styles.redirectBtn}
          onClick={() => navigate("/login")}
        >
          Already Registered? LOGIN
        </button>
      </div>
      <div className={styles.signUpMessageWrapper}>
        <h1> SIGNUP TO BARAK MART </h1>
        <img src={signUpImage} alt="form" className={styles.signUpMessageImg} />
      </div>
    </div>
  );
};

export { SignUp };
