import React, { useState } from "react";
import { useParams } from "react-router";
import { useDataContext } from "../../hook";
import { ProductCard, CategoryFilter } from "../index";
import styles from "../productList/ProductList.module.css";
import { BsFilter } from "../../icons/icon";
import filterStyle from "../filters/Filters.module.css";

const CategoryPage = () => {
  const {
    state: { productList },
  } = useDataContext();
  const [showFilters, setShowFilters] = useState(false);

  const { catId } = useParams();

  const productsOfParticularCategory = productList.filter(
    (product) => product.category === catId && product.inStock
  );

  return (
    <div className={styles.categoryPageWrapper}>
      <React.Fragment>
        <div className={filterStyle.filterContainer}>
          <button onClick={() => setShowFilters((showFilters) => !showFilters)}>
            <p className={filterStyle.filterTextStyle}>Filters</p>
            <BsFilter className={filterStyle.filterIcon} />
          </button>
        </div>

        <div
          className={
            showFilters ? filterStyle.showFilters : filterStyle.hideFilters
          }
        >
          <p>
            Lorem ipsum dolor, sit amet consectetur adipisicing elit. Ipsa eaque
            pariatur nemo quaerat tempore placeat aspernatur commodi iure
            dolorum, exercitationem minima aut possimus sequi autem, facilis
            magnam enim a perferendis? Voluptate doloribus, fugiat nesciunt
            accusantium ex aut distinctio eum nostrum? Repellat accusamus, fuga
            exercitationem adipisci, voluptas omnis doloribus modi maxime
            consequuntur ab tempora non fugit, culpa sit beatae quidem. Tempora
            assumenda ratione culpa minus ipsa! Voluptatibus et similique
            inventore, voluptatum earum impedit deserunt qui quae cumque dolores
            excepturi. Facere nostrum quia voluptatum esse cupiditate architecto
            consequatur modi at? Odit quam nostrum possimus molestiae minima ea
            delectus sed ut nemo minus.
          </p>
        </div>
      </React.Fragment>
      <div className={styles.productList}>
        {productsOfParticularCategory.map(
          (
            { _id, name, desc, image, price, fastDelivery, inStock, offer },
            index
          ) => {
            return (
              <ProductCard
                key={_id}
                productId={_id}
                index={index}
                dismissBtn={false}
                name={name}
                desc={desc}
                image={image}
                price={price}
                fastDelivery={fastDelivery}
                inStock={inStock}
                offer={offer}
              />
            );
          }
        )}
      </div>
    </div>
  );
};

export { CategoryPage };
