import React, { useState } from "react";
import signUpImage from "./undraw_window_shopping_b96y.svg";
import { useNavigate } from "react-router";
import {
  RiLoginCircleFill,
  AiFillEye,
  AiFillEyeInvisible,
} from "../icons/icon";
import styles from "./Login.module.css";

const SignUp = () => {
  const navigate = useNavigate();

  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  const handleSignUp = () => {};

  return (
    <div className={styles.loginDiv}>
      <div className={styles.loginWrapper}>
        <div className={`inpt-div flex flex-col ${styles.inputWrapper}`}>
          <input
            className={styles.inpt}
            type="text"
            required
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <label className={styles.inpt_label_secondary}>Username</label>
        </div>

        <div className={`inpt-div flex flex-col ${styles.inputWrapper}`}>
          <input
            className={`${styles.inpt}`}
            required
            type="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <label className={styles.inpt_label_secondary}>Email</label>
        </div>

        <div
          className={`inpt-div flex flex-col  ${styles.inputWrapper}`}
          style={{ border: "2px solid purple" }}
        >
          <input
            className={`${styles.inpt}`}
            style={{ border: "2px solid teal" }}
            required
            type={showPassword ? "text" : "password"}
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <label
            className={styles.inpt_label_secondary}
            style={{ border: "2px solid yellow" }}
          >
            Password
          </label>
          {showPassword ? (
            <AiFillEyeInvisible
              className={styles.passwordIcon}
              onClick={(showPassword) => console.log("CLICKED!!!!")}
            />
          ) : (
            <AiFillEye
              className={styles.passwordIcon}
              onClick={(showPassword) => console.log("CLICKED!!!!")}
            />
          )}
        </div>

        <div className={`inpt-div flex flex-col  ${styles.inputWrapper}`}>
          <input
            className={`${styles.inpt}`}
            required
            type={showConfirmPassword ? "text" : "password"}
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
          <label className={styles.inpt_label_secondary}>
            Confirm Password
          </label>
        </div>

        <button className={`btn btn-dark m-1`} onClick={handleSignUp}>
          SIGNUP
          <RiLoginCircleFill className={styles.signupIcon} />
        </button>
        <button
          className={styles.redirectBtn}
          onClick={() => navigate("/login")}
        >
          Already Registered? LOGIN
        </button>
      </div>
      <div className={styles.signUpMessageWrapper}>
        <h1> SIGNUP TO BARAK MART </h1>
        <img src={signUpImage} alt="form" className={styles.signUpMessageImg} />
      </div>
    </div>
  );
};

export { SignUp };
