import { useEffect } from "react";
import { useDataContext } from "./hook/index";
import { useNavigate } from "react-router-dom";
import { PRODUCT_API, WISHLIST_API, CART_API } from "./urls";

const Initialize = () => {
  const { fetchProductAndAdd } = useDataContext();
  let navigate = useNavigate();

  useEffect(() => {
    fetchProductAndAdd({
      url: `${PRODUCT_API}`,
      dispatchType: "ADD_TO_PRODUCT",
      listType: "products",
    });
    navigate("/products");
  }, []);

  useEffect(() => {
    fetchProductAndAdd({
      url: `${WISHLIST_API}`,
      dispatchType: "ADD_TO_WISHLIST",
      listType: "wishLists",
    });
  }, []);

  useEffect(() => {
    fetchProductAndAdd({
      url: `${CART_API}`,
      dispatchType: "ADD_TO_CART",
      listType: "cartLists",
    });
  }, []);
};

export { Initialize };
