import React from "react";
import { useParams } from "react-router";
import { useDataContext } from "../../hook";
import { ProductCard } from "../index";
import styles from "../productList/ProductList.module.css";

const CategoryPage = () => {
  const {
    state: { productList },
  } = useDataContext();

  const { catId } = useParams();

  const productsOfParticularCategory = productList.filter(
    (product) => product.category === catId
  );

  console.log("CategoryPage", { catId }, { productsOfParticularCategory });

  return (
    <div className={styles.productList}>
      {productsOfParticularCategory.map(
        (
          { _id, name, desc, image, price, fastDelivery, inStock, offer },
          index
        ) => {
          return (
            <ProductCard
              key={_id}
              productId={_id}
              index={index}
              dismissBtn={false}
              name={name}
              desc={desc}
              image={image}
              price={price}
              fastDelivery={fastDelivery}
              inStock={inStock}
              offer={offer}
            />
          );
        }
      )}
    </div>
  );
};

export { CategoryPage };
