import React from "react";
import { useParams } from "react-router";
import { useDataContext } from "../../hook";
import { ProductCard } from "../index";
import styles from "../productList/ProductList.module.css";
import { BackButton } from "../index";
const CategoryPage = () => {
  const {
    state: { productList },
  } = useDataContext();

  const { catId } = useParams();

  const productsOfParticularCategory = productList.filter(
    (product) => product.category === catId && product.inStock
  );

  console.log({ productsOfParticularCategory });

  return (
    <div className={styles.categoryPageWrapper}>
      <BackButton />
      <h2>{catId}</h2>
      <div className={styles.productList}>
        {productsOfParticularCategory.map(
          (
            { _id, name, desc, image, price, fastDelivery, inStock, offer },
            index
          ) => {
            return (
              <ProductCard
                key={_id}
                productId={_id}
                index={index}
                dismissBtn={false}
                name={name}
                desc={desc}
                image={image}
                price={price}
                fastDelivery={fastDelivery}
                inStock={inStock}
                offer={offer}
              />
            );
          }
        )}
      </div>
    </div>
  );
};

export { CategoryPage };

