import React from "react";
import loginImage from "./undraw_window_shopping_b96y.svg";
import styles from "./Home.module.css";
import { AiOutlineArrowRight } from "../icons/icon";
import { useDataContext } from "../hook/index";

const Home = () => {
  const {
    state: { productList },
  } = useDataContext();
  const categories = ["T-shirt", "Face Mask", "Mugs", "Hoodie", "Beanie"];

  return (
    <div className={styles.homeWrapper}>
      <div>
        <h1>
          Discover the best finance content through timeless books by top
          authors
        </h1>
        <p>
          Thoughtfully curated collection to help you get started with your
          personal finance journey.
        </p>
        <button>
          Start Shopping <AiOutlineArrowRight />{" "}
        </button>
      </div>
      <div>
        <img src={loginImage} alt="home" />
      </div>

      <div className={styles.categoryWrapper}>
        <h2>Categories</h2>
      </div>
    </div>
  );
};

export { Home };
