import React, { useEffect } from "react";
import loginImage from "./undraw_window_shopping_b96y.svg";
import styles from "./Home.module.css";
import { AiOutlineArrowRight } from "../icons/icon";
import { useDataContext } from "../hook/index";
import { ProductCard } from "../components";

const Home = () => {
  const {
    state: { productList },
  } = useDataContext();
  const categories = ["T-shirt", "Face Mask", "Mugs", "Hoodie", "Beanie"];

  // const topSellingItem = [
  //   productList[1],
  //   productList[7],
  //   productList[10],
  //   productList[14],
  //   productList[18],
  // ];

  // const topSellingItem = productList.filter((product, index) => index === 1);

  // console.log("Home", { topSellingItem });
  return (
    <div className={styles.homeWrapper}>
      <div>
        <h1>
          Discover the best finance content through timeless books by top
          authors
        </h1>
        <p>
          Thoughtfully curated collection to help you get started with your
          personal finance journey.
        </p>
        <button>
          Start Shopping <AiOutlineArrowRight />{" "}
        </button>
      </div>
      <div>
        <img src={loginImage} alt="home" />
      </div>

      <div className={styles.categoryWrapper}>
        <h2>Categories</h2>
        {categories.map((category) => {
          return (
            <div>
              <p>{category}</p>
            </div>
          );
        })}
      </div>

      <div className={styles.ofTheWeekWrapper}>
        <h2>Top Selling Items of the Week</h2>
        {/* <ProductCard
          key={topSellingItem._id}
          productId={topSellingItem._id}
          index={1}
          dismissBtn={false}
          name={topSellingItem.name}
          desc={topSellingItem.desc}
          image={topSellingItem.image}
          price={topSellingItem.price}
          fastDelivery={topSellingItem.fastDelivery}
          inStock={topSellingItem.inStock}
          offer={topSellingItem.offer}
        /> */}
      </div>
    </div>
  );
};

export { Home };
