import React, { useState } from "react";
import { useUserContext, useAuthContext } from "../hook/index";

import {
  RiLoginCircleFill,
  AiFillEye,
  AiFillEyeInvisible,
} from "react-icons/ri";
import loginImage from "./undraw_web_shopping_dd4l.svg";

import styles from "./Login.module.css";
import { useNavigate } from "react-router";
import { useAuth } from "../hook/index";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [isShowPassword, setIsShowPassword] = useState(false);

  const { handleUserCredentials } = useAuthContext();
  const { state, dispatch } = useUserContext();
  const { handleLogin } = useAuth();

  const navigate = useNavigate();

  return (
    <div className={styles.loginDiv}>
      <div className={styles.signUpMessageWrapper}>
        <img src={loginImage} alt="form" className={styles.signUpMessageImg} />

        <h1> BARAK MART </h1>
        <p> </p>
      </div>
      <div className={styles.loginWrapper}>
        <div className={`inpt-div flex flex-col ${inputWrapper}`}>
          <input
            className={styles.inpt}
            type="text"
            required
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <label className={styles.inpt_label_secondary}>Username</label>
        </div>

        <div className={`inpt-div flex flex-col ${inputWrapper}`}>
          <input
            className={styles.inpt}
            styles={{ margin: "1em 0em" }}
            required
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <label className={styles.inpt_label_secondary}>Password</label>
        </div>

        <button
          className={`btn btn-dark m-1`}
          onClick={() => handleLogin(username, password)}
        >
          LOGIN
          <RiLoginCircleFill className={styles.signupIcon} />
        </button>
        <button
          className={styles.redirectBtn}
          onClick={() => navigate("/signup")}
        >
          New User? SIGNUP
        </button>
      </div>
    </div>
  );
};

export { Login };
