import React from "react";
import { useDataContext } from "../../hook/index";
import { Checkout, CartCard } from "../index";
import styles from "./Cart.module.css";

const Cart = () => {
  const {
    state: { cartList },
  } = useDataContext();

  if (state.cartList.hasOwnProperty("userId")) {
    if (state.cartList.cartItems.length > 0) {
      console.log(state.cartList.cartItems);
    }
  }

  return (
    <div className={styles.cartDiv}>
      <div className={styles.cartList}>
        {cartList.hasOwnProperty("userId") &&
          cartList.cartItems.length > 0 &&
          cartList.cartItems.map(
            (
              {
                _id: {
                  _id,
                  name,
                  desc,
                  image,
                  price,
                  fastDelivery,
                  inStock,
                  offer,
                  quantity,
                },
              },
              index
            ) => {
              return (
                <CartCard
                  key={_id}
                  index={index}
                  type="cartList"
                  productId={_id}
                  dismissBtn={true}
                  name={name}
                  desc={desc}
                  image={image}
                  price={price}
                  fastDelivery={fastDelivery}
                  inStock={inStock}
                  offer={offer}
                  quantity={quantity}
                />
              );
            }
          )}
      </div>
      <Checkout />
    </div>
  );
};

export { Cart };
