import React, { useState } from "react";
import signUpImage from "./undraw_window_shopping_b96y.svg";
import { useNavigate } from "react-router";
import {
  checkInputField,
  validateEmail,
  validatePassword,
  validateUsername,
  isMatch,
} from "../utils";

import {
  RiLoginCircleFill,
  AiFillEye,
  AiFillEyeInvisible,
} from "../icons/icon";
import styles from "./Login.module.css";
import { IconContext } from "react-icons";

const SignUp = () => {
  const navigate = useNavigate();

  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [error, setError] = useState("");

  const handleSignUp = () => {
    checkInputField(username) === "EMPTY"
      ? setError("username cannot be empty")
      : setError("");
    checkInputField(email) === "EMPTY"
      ? setError("email cannot be empty")
      : setError("");
    checkInputField(password) === "EMPTY"
      ? setError("password cannot be empty")
      : setError("");
    checkInputField(confirmPassword) === "EMPTY"
      ? setError("confirm password cannot be empty")
      : setError("");
  };

  return (
    <div className={styles.loginDiv}>
      <div className={styles.loginWrapper}>
        <div className={`inpt-div flex flex-col ${styles.inputWrapper}`}>
          <input
            className={styles.inpt}
            type="text"
            required
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <label className={styles.inpt_label_secondary}>Username</label>
        </div>

        <div className={`inpt-div flex flex-col ${styles.inputWrapper}`}>
          <input
            className={`${styles.inpt}`}
            required
            type="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <label className={styles.inpt_label_secondary}>Email</label>
        </div>

        <div className={`inpt-div flex flex-col  ${styles.inputWrapper}`}>
          <input
            className={`${styles.inpt}`}
            required
            type={showPassword ? "text" : "password"}
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <label className={styles.inpt_label_secondary}>Password</label>
          <IconContext.Provider value={{ size: "1.3em" }}>
            {showPassword ? (
              <AiFillEyeInvisible
                className={styles.passwordIcon}
                onClick={(e) =>
                  setShowPassword((showPassword) => !showPassword)
                }
              />
            ) : (
              <AiFillEye
                className={styles.passwordIcon}
                onClick={(e) =>
                  setShowPassword((showPassword) => !showPassword)
                }
              />
            )}
          </IconContext.Provider>
        </div>

        <div className={`inpt-div flex flex-col  ${styles.inputWrapper}`}>
          <input
            className={`${styles.inpt}`}
            required
            type={showConfirmPassword ? "text" : "password"}
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
          <label className={styles.inpt_label_secondary}>
            Confirm Password
          </label>
          <IconContext.Provider value={{ size: "1.3em" }}>
            {showConfirmPassword ? (
              <AiFillEyeInvisible
                className={styles.passwordIcon}
                onClick={(e) =>
                  setShowConfirmPassword(
                    (showConfirmPassword) => !showConfirmPassword
                  )
                }
              />
            ) : (
              <AiFillEye
                className={styles.passwordIcon}
                onClick={(e) =>
                  setShowConfirmPassword(
                    (showConfirmPassword) => !showConfirmPassword
                  )
                }
              />
            )}
          </IconContext.Provider>
        </div>

        <button className={`btn btn-dark m-1`} onClick={handleSignUp}>
          SIGNUP
          <RiLoginCircleFill className={styles.signupIcon} />
        </button>
        {error.length > 0 && <span>{error}</span>}
        <button
          className={styles.redirectBtn}
          onClick={() => navigate("/login")}
        >
          Already Registered? LOGIN
        </button>
      </div>
      <div className={styles.signUpMessageWrapper}>
        <h1> SIGNUP TO BARAK MART </h1>
        <img src={signUpImage} alt="form" className={styles.signUpMessageImg} />
      </div>
    </div>
  );
};

export { SignUp };
