import React, { useState } from "react";
import { useUserContext, useAuthContext } from "../hook/index";

import { RiLoginCircleFill } from "react-icons/ri";
import loginImage from "./undraw_web_shopping_dd4l.svg";

import styles from "./Login.module.css";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isSignUp, setSignUp] = useState(false);

  const { handleUserCredentials } = useAuthContext();
  const { state, dispatch } = useUserContext();

  const handleLogin = () => {
    handleUserCredentials(username, password, state.users);

    setUsername("");
    setPassword("");
  };

  const handleSignUp = () => {
    dispatch({
      type: "ADD_USERS_TO_LIST",
      payload: { username: username, password: password },
    });

    setUsername("");
    setPassword("");

    setSignUp((value) => !value);
  };

  return (
    <div className={styles.loginDiv}>
      <div
        style={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <div
          className={`inpt-div flex flex-col`}
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            width: "100%",
            margin: "1em 0em",
          }}
        >
          <input
            className={styles.inpt}
            type="text"
            required
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <label className={styles.inpt_label_secondary}>Enter Username</label>
        </div>

        <div
          className={`inpt-div flex flex-col`}
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            width: "100%",
            margin: "1em 0em",
          }}
        >
          <input
            className={styles.inpt}
            styles={{ margin: "1em 0em" }}
            required
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <label className={styles.inpt_label_secondary}>Enter Password</label>
        </div>

        <button className={`btn btn-dark m-1`} onClick={handleSignUp}>
          SIGNUP
          <RiLoginCircleFill
            style={{
              fontSize: "1.1rem",
              transform: "translate(40%,25%)",
              margin: "0em",
              padding: "0em",
            }}
          />
        </button>
        <button
          style={{
            fontSize: "0.7rem",
            fontWeight: "900",
            letterSpacing: "1.5px",
          }}
          onClick={() => setSignUp((value) => !value)}
        >
          LOGIN
        </button>
      </div>
      <div
        style={{
          backgroundColor: "rgb(80, 80, 80)",
          color: "#fff",
          display: "flex",
          padding: "3em 0em",
          justifyContent: "center",
          flexDirection: "column",
        }}
      >
        <h1> SIGNUP TO BARAK MART </h1>
        <img
          src={signUpImage}
          alt="form"
          style={{ width: "50%", alignSelf: "center", margin: "0.8em 0em" }}
        />
      </div>
    </div>
  );
};

export { Login };
