import React, { useState } from "react";
import { useUserContext, useAuthContext } from "../hook/index";

import { RiLoginCircleFill } from "react-icons/ri";
import loginImage from "./undraw_web_shopping_dd4l.svg";

import styles from "./Login.module.css";
import { useNavigate } from "react-router";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [isShowPassword, setIsShowPassword] = useState(false);

  const { handleUserCredentials } = useAuthContext();
  const { state, dispatch } = useUserContext();

  const navigate = useNavigate();

  const handleLogin = () => {
    handleUserCredentials(username, password, state.users);

    setUsername("");
    setPassword("");
  };

  const handleSignUp = () => {
    dispatch({
      type: "ADD_USERS_TO_LIST",
      payload: { username: username, password: password },
    });

    setUsername("");
    setPassword("");
  };

  return (
    <div className={styles.loginDiv}>
      <div className={styles.signUpMessageWrapper}>
        <img src={loginImage} alt="form" className={styles.signUpMessageImg} />

        <h1> BARAK MART </h1>
        <p> </p>
      </div>
      <div className={styles.loginWrapper}>
        <div
          className={`inpt-div flex flex-col`}
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            width: "100%",
            margin: "1em 0em",
          }}
        >
          <input
            className={styles.inpt}
            type="text"
            required
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <label className={styles.inpt_label_secondary}>Username</label>
        </div>

        <div
          className={`inpt-div flex flex-col`}
          style={{
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            width: "100%",
            margin: "1em 0em",
          }}
        >
          <input
            className={styles.inpt}
            styles={{ margin: "1em 0em" }}
            required
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <label className={styles.inpt_label_secondary}>Password</label>
        </div>

        <button className={`btn btn-dark m-1`} onClick={handleLogin}>
          LOGIN
          <RiLoginCircleFill
            style={{
              fontSize: "1.1rem",
              transform: "translate(40%,25%)",
              margin: "0em",
              padding: "0em",
            }}
          />
        </button>
        <button
          style={{
            fontSize: "0.7rem",
            fontWeight: "900",
            letterSpacing: "1.5px",
          }}
          onClick={() => navigate("/signup")}
        >
          New User? SIGNUP
        </button>
      </div>
    </div>
  );
};

export { Login };
