import React from "react";
import "./styles.css";
import { Login, PrivateRoute, SignUp } from "./pages/index";
import { Routes, Route } from "react-router-dom";
import { useAuthContext } from "./hook/index";
import {
  ProductList,
  Navbar,
  Filters,
  Cart,
  Wishlist,
} from "./components/index";

import { Initialize } from "./utils";

export default function App() {
  const { isLogin } = useAuthContext();

  Initialize();

  return (
    <div className="App">
      <Navbar />
      <Routes>
        <PrivateRoute
          isLogin={isLogin}
          path="/products"
          element={
            <>
              <Filters /> <ProductList />
            </>
          }
        />
        <PrivateRoute isLogin={isLogin} path="/cart" element={<Cart />} />
        <PrivateRoute
          isLogin={isLogin}
          path="/wishlist"
          element={<Wishlist />}
        />
        {/* <div className="loginComponent"> */}
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<SignUp />} />
        {/* </div> */}
      </Routes>
    </div>
  );
}
